DROP TABLE IF EXISTS t_career
CREATE TABLE `t_career` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `Content` longtext NOT NULL,  `NoteTime` datetime NOT NULL,  `Language` varchar(10) NOT NULL,  PRIMARY KEY (`id`),  KEY `Language` (`Language`)) ENGINE=MyISAM DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS t_config
CREATE TABLE `t_config` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `name` varchar(255) NOT NULL DEFAULT '',  `code` varchar(255) NOT NULL DEFAULT '',  `value` varchar(255) NOT NULL DEFAULT '',  PRIMARY KEY (`id`),  KEY `name` (`name`)) ENGINE=MyISAM AUTO_INCREMENT=52 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS t_content
CREATE TABLE `t_content` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `Called` varchar(120) NOT NULL DEFAULT '',  `NoteTime` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',  `Content` longtext NOT NULL,  `Language` varchar(10) NOT NULL DEFAULT '',  `keyword` varchar(255) NOT NULL,  `bj` varchar(255) NOT NULL,  `moemo` varchar(255) NOT NULL,  `cmtype` varchar(255) NOT NULL,  PRIMARY KEY (`id`),  KEY `Called` (`Called`)) ENGINE=MyISAM AUTO_INCREMENT=43 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS t_download
CREATE TABLE `t_download` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `Called` varchar(120) NOT NULL,  `NoteTime` datetime NOT NULL,  `FileURL` varchar(120) NOT NULL,  `Memo` text NOT NULL,  `IsShow` tinyint(4) NOT NULL,  `IsCommend` tinyint(4) NOT NULL,  `OrderBy` int(11) NOT NULL,  `TypeID` int(11) NOT NULL,  `FileSize` int(11) NOT NULL,  `Language` varchar(10) NOT NULL,  PRIMARY KEY (`id`),  KEY `TypeID` (`TypeID`,`OrderBy`)) ENGINE=MyISAM AUTO_INCREMENT=19 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS t_downloadtype
CREATE TABLE `t_downloadtype` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `Called` varchar(120) NOT NULL,  `NoteTime` datetime NOT NULL,  `Memo` text NOT NULL,  `Language` varchar(10) NOT NULL,  PRIMARY KEY (`id`),  KEY `Called` (`Called`,`Language`)) ENGINE=MyISAM AUTO_INCREMENT=5 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS t_feedback
CREATE TABLE `t_feedback` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `Name` varchar(255) NOT NULL DEFAULT '',  `Content` text NOT NULL,  `NoteTime` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',  `IP` varchar(20) NOT NULL DEFAULT '',  `Language` varchar(10) NOT NULL DEFAULT '',  PRIMARY KEY (`id`),  KEY `Language` (`Language`)) ENGINE=MyISAM DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS t_fields
CREATE TABLE `t_fields` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `Called` varchar(255) NOT NULL DEFAULT '',  `FieldName` varchar(100) NOT NULL DEFAULT '',  `DataType` varchar(20) NOT NULL DEFAULT '',  `UiType` varchar(20) NOT NULL DEFAULT '',  `DefaultValue` tinytext NOT NULL,  `TypeName` varchar(50) DEFAULT NULL,  PRIMARY KEY (`id`),  KEY `TypeName` (`TypeName`)) ENGINE=MyISAM AUTO_INCREMENT=22 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS t_flow
CREATE TABLE `t_flow` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `NoteTime` datetime NOT NULL,  `IP` varchar(20) NOT NULL,  `ViewPage` varchar(255) NOT NULL,  `RefPage` varchar(255) NOT NULL,  `OS` varchar(20) NOT NULL,  `Browser` varchar(20) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=51 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS t_global
CREATE TABLE `t_global` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `Webname` varchar(500) NOT NULL,  `Title` varchar(100) NOT NULL,  `Keywords` varchar(500) NOT NULL,  `Description` varchar(500) NOT NULL,  `Beian` varchar(100) NOT NULL,  `Upload` varchar(50) NOT NULL,  `Web` varchar(50) NOT NULL,  `Language` varchar(10) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS t_guestbook
CREATE TABLE `t_guestbook` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `Subject` varchar(255) NOT NULL,  `UserName` varchar(60) NOT NULL,  `Mail` varchar(120) NOT NULL,  `Company` varchar(255) NOT NULL,  `Web` varchar(120) NOT NULL,  `Content` text NOT NULL,  `IsShow` tinyint(4) NOT NULL,  `NoteTime` datetime NOT NULL,  `IP` varchar(20) NOT NULL,  `Reply` text NOT NULL,  `Language` varchar(10) NOT NULL,  PRIMARY KEY (`id`),  KEY `IsShow` (`IsShow`,`Language`)) ENGINE=MyISAM AUTO_INCREMENT=17 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS t_img
CREATE TABLE `t_img` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `ProName` varchar(120) NOT NULL,  `PicUrl` varchar(120) NOT NULL,  `BigUrl` varchar(120) NOT NULL,  `NoteTime` datetime NOT NULL,  `IsShow` tinyint(4) NOT NULL,  `IsCommend` tinyint(4) NOT NULL,  `OrderBy` int(11) NOT NULL,  `ProID` int(11) NOT NULL,  `Language` varchar(10) NOT NULL,  PRIMARY KEY (`id`),  KEY `ProID` (`ProID`,`OrderBy`)) ENGINE=MyISAM AUTO_INCREMENT=170 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS t_job
CREATE TABLE `t_job` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `Position` varchar(100) NOT NULL,  `Specialty` varchar(100) NOT NULL,  `Sex` varchar(10) NOT NULL,  `Num` varchar(20) NOT NULL,  `Age` varchar(20) NOT NULL,  `Educational` varchar(100) NOT NULL,  `Experience` varchar(100) NOT NULL,  `Salary` varchar(100) NOT NULL,  `Memo` text NOT NULL,  `NoteTime` datetime NOT NULL,  `EndTime` date NOT NULL,  `IsShow` tinyint(4) NOT NULL,  `IsCommend` tinyint(4) NOT NULL,  `OrderBy` int(11) NOT NULL,  `Language` varchar(10) NOT NULL,  PRIMARY KEY (`id`),  KEY `OrderBy` (`OrderBy`,`Language`)) ENGINE=MyISAM AUTO_INCREMENT=6 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS t_log
CREATE TABLE `t_log` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `IP` varchar(20) NOT NULL DEFAULT '',  `Time` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',  `Description` varchar(255) NOT NULL DEFAULT '',  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=425 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS t_news
CREATE TABLE `t_news` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `Title` varchar(255) NOT NULL DEFAULT '',  `NoteTime` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',  `Content` longtext NOT NULL,  `UserName` varchar(100) NOT NULL DEFAULT '',  `IsShow` tinyint(4) NOT NULL DEFAULT '0',  `IsCommend` tinyint(4) NOT NULL DEFAULT '0',  `OrderBy` int(11) NOT NULL DEFAULT '0',  `NewType` int(10) unsigned NOT NULL DEFAULT '0',  `Hits` int(10) unsigned NOT NULL DEFAULT '0',  `Language` varchar(10) NOT NULL DEFAULT '',  `Parent` int(11) NOT NULL DEFAULT '0',  `SmallPic` varchar(100) NOT NULL DEFAULT '',  PRIMARY KEY (`id`),  KEY `NewType` (`NewType`,`Title`,`OrderBy`)) ENGINE=MyISAM AUTO_INCREMENT=11 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS t_newtype
CREATE TABLE `t_newtype` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `Called` varchar(100) NOT NULL DEFAULT '',  `Language` varchar(10) NOT NULL DEFAULT '',  PRIMARY KEY (`id`),  KEY `Called` (`Called`,`Language`)) ENGINE=MyISAM AUTO_INCREMENT=5 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS t_order
CREATE TABLE `t_order` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `Company` varchar(255) NOT NULL DEFAULT '',  `Name` varchar(60) NOT NULL DEFAULT '',  `Tel` varchar(40) NOT NULL DEFAULT '',  `Mobile` varchar(40) NOT NULL DEFAULT '',  `Mail` varchar(120) NOT NULL DEFAULT '',  `Address` varchar(255) NOT NULL DEFAULT '',  `Memo` text NOT NULL,  `IP` varchar(20) NOT NULL DEFAULT '',  `NoteTime` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',  `Language` varchar(10) NOT NULL DEFAULT '',  PRIMARY KEY (`id`),  KEY `Language` (`Language`)) ENGINE=MyISAM DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS t_orderdetail
CREATE TABLE `t_orderdetail` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `OrderID` int(10) unsigned NOT NULL DEFAULT '0',  `ProductID` int(10) unsigned NOT NULL DEFAULT '0',  `ProductName` varchar(120) NOT NULL DEFAULT '',  `Nums` int(11) NOT NULL DEFAULT '0',  `Price` float NOT NULL DEFAULT '0',  `Memo` text NOT NULL,  PRIMARY KEY (`id`),  KEY `OrderID` (`OrderID`)) ENGINE=MyISAM DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS t_pic
CREATE TABLE `t_pic` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `PicName` varchar(120) NOT NULL,  `PicUrl` varchar(120) NOT NULL,  `BigUrl` varchar(120) NOT NULL,  `NoteTime` datetime NOT NULL,  `IsShow` tinyint(4) NOT NULL,  `IsCommend` tinyint(4) NOT NULL,  `OrderBy` int(11) NOT NULL,  `TypeID` int(11) NOT NULL,  `Language` varchar(10) NOT NULL,  PRIMARY KEY (`id`),  KEY `TypeID` (`TypeID`,`OrderBy`)) ENGINE=MyISAM AUTO_INCREMENT=17 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS t_pictype
CREATE TABLE `t_pictype` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `Called` varchar(80) NOT NULL DEFAULT '',  `NoteTime` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',  `Memo` text NOT NULL,  `Language` varchar(10) NOT NULL DEFAULT '',  PRIMARY KEY (`id`),  KEY `Called` (`Called`,`Language`)) ENGINE=MyISAM AUTO_INCREMENT=8 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS t_products
CREATE TABLE `t_products` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `ProName` varchar(200) NOT NULL DEFAULT '',  `Content` longtext NOT NULL,  `Memo` text NOT NULL,  `NoteTime` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',  `SmallPic` varchar(100) NOT NULL DEFAULT '',  `IsShow` tinyint(4) NOT NULL DEFAULT '0',  `IsCommend` tinyint(4) NOT NULL DEFAULT '0',  `OrderBy` int(11) NOT NULL DEFAULT '0',  `TypeID` int(11) NOT NULL DEFAULT '0',  `Hits` int(11) NOT NULL DEFAULT '0',  `Language` varchar(10) NOT NULL DEFAULT '',  PRIMARY KEY (`id`),  KEY `ProName` (`ProName`,`TypeID`)) ENGINE=MyISAM AUTO_INCREMENT=10 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS t_protype
CREATE TABLE `t_protype` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `Called` varchar(100) NOT NULL DEFAULT '',  `PicUrl` varchar(100) NOT NULL DEFAULT '',  `OrderBy` int(11) NOT NULL DEFAULT '0',  `IsShow` tinyint(4) NOT NULL DEFAULT '0',  `Memo` text NOT NULL,  `PID` int(11) NOT NULL DEFAULT '0',  `TypeLevel` tinyint(4) NOT NULL DEFAULT '0',  `Language` varchar(10) NOT NULL DEFAULT '',  `ParentPath` varchar(255) NOT NULL DEFAULT '',  `NoteTime` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',  PRIMARY KEY (`id`),  KEY `PID` (`PID`,`Called`)) ENGINE=MyISAM AUTO_INCREMENT=7 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS t_result
CREATE TABLE `t_result` (  `ID` int(10) unsigned NOT NULL AUTO_INCREMENT,  `Result` varchar(255) NOT NULL COMMENT '投票答案标题',  `NoteTime` datetime NOT NULL COMMENT '插入时间',  `VoteTime` datetime NOT NULL COMMENT '投票时间',  `VoteId` int(10) NOT NULL COMMENT '投票问题ID',  `Language` varchar(10) NOT NULL COMMENT '语言',  `num` int(10) NOT NULL,  PRIMARY KEY (`ID`)) ENGINE=MyISAM AUTO_INCREMENT=18 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS t_user
CREATE TABLE `t_user` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `UserName` varchar(40) NOT NULL,  `Password` varchar(40) NOT NULL,  `NoteTime` datetime NOT NULL,  `IsLock` tinyint(4) NOT NULL,  `Called` varchar(60) NOT NULL,  `Company` varchar(255) NOT NULL,  `Tel` varchar(40) NOT NULL,  `Mobile` varchar(40) NOT NULL,  `Mail` varchar(120) NOT NULL,  `Language` varchar(10) NOT NULL,  PRIMARY KEY (`id`),  KEY `UserName` (`UserName`,`Password`)) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS t_viewnum
CREATE TABLE `t_viewnum` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `nums` int(10) unsigned NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS t_vote
CREATE TABLE `t_vote` (  `ID` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '自增主键',  `Subject` varchar(255) NOT NULL COMMENT '标题',  `NoteTime` datetime NOT NULL COMMENT '添加日期',  `Language` varchar(10) NOT NULL COMMENT '语言',  PRIMARY KEY (`ID`)) ENGINE=MyISAM AUTO_INCREMENT=12 DEFAULT CHARSET=utf8
